import logo from './logo.svg';
import './App.css';
import Money from './Money';
import React, {useState} from 'react';
import { Button, Input, FormFeedback } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';



var bank_type = [

  {
          type : 20,
          amount: 200,
      },
  {
          type : 50,
          amount: 50,
      },
      {
          type : 100,
          amount: 50,
      },
      {
          type : 500,
          amount: 100,
      },
      {
          type : 1000,
          amount: 100,
      }


];

var coin_type = [

  {
          type : 1,
          amount: 500,
      },
  {
          type : 2,
          amount: 500,
      },
      {
          type : 5,
          amount: 500,
      },
      {
          type : 10,
          amount: 500,
      },



];


function App() {

  const [screen, setScreen] = useState('main');
  const [getBill, setGetBill] = useState([]);
  const [getCoin, setGetCoin] = useState([]);
  const [valErr, setValErr] = useState(false);
  const [withdrawAmount, setWithdraw] = useState('');




  const [bank, setBank] = useState(bank_type);
  const [coin, setCoin] = useState(coin_type);



  function sub(){
      const billArray = [['20',0],['50',0],['100',0],['500',0],['1000',0]];
      const coinArray = [['1',0],['2',0],['5',0],['10',0]]
      let total = 0;
      coin.forEach(value => {
          total += value.type * value.amount;
      });

      bank.forEach(value => {
        total += value.type * value.amount;
    });

      console.log(total);
      setWithdraw(document.getElementById('amount_input').value);
      let amount = parseInt(document.getElementById('amount_input').value);

      if (!Number(amount)) {
          return;
      }
      if ( amount > total) {
        setScreen('error');
        return
      }
      while (amount > 0) {

          if(Math.floor(amount / 1000 ) !== 0 && bank_type[4].amount > 0){
              amount -= 1000;
              bank_type[4].amount -= 1;
              billArray[4][1] += 1;
          }
          else if(Math.floor(amount / 500 ) !== 0 && bank_type[3].amount >0){
              amount -= 500;
              bank_type[3].amount -= 1;
              billArray[3][1] += 1;
          }
          else if(Math.floor(amount / 100 ) !== 0 && bank_type[2].amount > 0){
              amount -= 100;
              bank_type[2].amount -= 1;
              billArray[2][1] += 1;
          }
          else if(Math.floor(amount / 50 ) !== 0 && bank_type[1].amount > 0){
              amount -= 50;
              bank_type[1].amount -= 1;
              billArray[1][1] += 1;
          }

          else if(Math.floor(amount / 20 ) !== 0 && bank_type[0].amount >0){
              amount -= 20;
              bank_type[0].amount -= 1;
              billArray[0][1] += 1;
          }

          else if(Math.floor(amount / 10 ) !== 0 && coin_type[3].amount > 0){
              amount -= 10;
              coin_type[3].amount -= 1;
              coinArray[3][1] += 1;
          }
          else if(Math.floor(amount / 5) !== 0 && coin_type[2].amount > 0){
              amount -= 5;
              coin_type[2].amount -= 1;
              coinArray[2][1] += 1;
          }

          else if(Math.floor(amount / 2 ) !== 0 && coin_type[1].amount > 0){
              amount -= 2;
              coin_type[1].amount -= 1;
              coinArray[1][1] += 1;
          }

          else if(Math.floor(amount / 1 ) !== 0 && coin_type[0].amount > 0){

              amount -= 1;
              coin_type[0].amount -= 1;
              coinArray[0][1] += 1;
          }




      }

        setScreen('result');
        setGetBill(billArray.filter((bill) => bill[1] != 0));
        setGetCoin(coinArray.filter((coin) => coin[1] != 0));
        setBank(bank_type);
        setCoin(coin_type);




  }


  function checkValid(e){
    let variable = e.target.value;

    if(!Number(variable)) {
      console.log(Number(variable));
      setValErr(true);
    }
    else{
      setValErr(false);
    }
  }

  const withdrawBtn = <Button color="danger" size="lg" onClick={sub}>Withdraw</Button>;
  const doneBtn = <Button color="success" size="lg" onClick={() => setScreen('main')}>Done</Button>;
  const errorBtn =   <Button color="warning" size="lg" onClick={() => setScreen('main')}>Back</Button>;

  const mainScreen = (

    <div className='mainScreen'>
      <center><p className='screenTitle'>Amount to withdraw</p></center>
      <center><Input bsSize="lg" placeholder='Enter amount' id='amount_input' invalid={valErr} onChange={checkValid}/></center>
      <center><p className='inputStat'>Input Invalid</p></center>
    </div>




);

const resultScreen = (
  <div className='inputField'>
  <center><p className='screenTitle'>Summary</p></center>
  <center>
      <tr>
          <th>
              Withdraw:
          </th>
          <th>
              à¸¿{withdrawAmount}
          </th>
      </tr>
      { getCoin.length === 0 ? <tr></tr> :
              <tr>
              <th>
                  Coin
              </th>
              <th>
                  Amount
              </th>
          </tr>

      }


          {getCoin.map((element) => 
                      <tr>
                          <td>{element[0]}</td>
                          <td>{element[1]}</td>
                      </tr>
              )}
           { getBill.length === 0 ? <tr></tr> :
              <tr>
              <th>
                  Bill
              </th>
              <th>
                  Amount
              </th>
          </tr>

      }

              {getBill.map((element) => 
                  <tr>
                      <td>{element[0]}</td>
                      <td>{element[1]}</td>
                  </tr>
          )}

      </center>
</div>
);


const errorScreen = (
    <h1>Not Enough Money</h1>
);



  return (
    <div className="App">
      <div className="AppBase">
        <div className='titleContainer'>
            <p className='title'>ATM V2</p>
        </div>

        <div className='moneyContainer'>
            <Money bank={bank} coin={coin}/>

            </div>
        <div className='contentContainer'> 

          <div className='Content'>
              {screen === 'main'? mainScreen : screen === 'result' ? resultScreen : errorScreen}
          </div>

        </div>
        <div className='buttonContainer'> 
          <div className='button'>
            {screen === 'main'? withdrawBtn : screen === 'result' ? doneBtn : errorBtn}
          </div>
          </div>
      </div>
      </div>


  );
}

export default App;
